import React, { Component } from "react";
import { Paper, Typography, TextField, Button } from "@material-ui/core";
import { List, ListItem, ListItemText, ListItemSecondaryAction, IconButton } from "@material-ui/core";

class App extends Component {
    state = {
        exercises: [
            { id: 1, title: "Bench Press" },
            { id: 2, title: "Deadlift" },
            { id: 3, title: "Squats" }
        ],
        title: ""
    };

    handleChange = ({ target: { name, value } }) =>
        this.setState({ [name]: value });

    handleCreate = e => {
        e.preventDefault();

        if (this.state.title) {
            this.setState(({ exercises, title }) => ({
                exercises: [
                    ...exercises,
                    {
                        title,
                        id: Date.now()
                    }
                ],
                title: ""
            }));
        }
    };

    handleDelete = (id) => this.setState(({exercises}) => ({exercises: exercises.filter(ex => ex.id !== id)}))

    render() {
        const { title, exercises } = this.state;
        return (
            <>
                <Paper>
                    <Typography variant="h1" align="center" gutterBottom>
                        Exercises

                    </Typography>
                    <form action="#" onSubmit={this.handleCreate} align="center">
                            <TextField
                                name="title"
                                label="Exercise"
                                value={title}
                                onChange={this.handleChange}
                                margin="dense"
                            />
                            <Button
                                type="submit"
                                color="primary"
                                variant="contained"
                                margin="none"
                            >
                                Create
                            </Button>
                        </form>
                    <List>
                        {exercises.map(({ title, id }) => (
                            <ListItem key={id} >
                                <ListItemText primary={title} />
                                <ListItemSecondaryAction><IconButton color="primary" onClick={() => this.handleDelete(id)}>{/* ??? */}  </IconButton></ListItemSecondaryAction>
                            </ListItem>
                        ))}
                    </List>
                </Paper>
            </>
        );
    }
}

export default App;
