import React from "react";
import {
    Grid,
    Paper,
    Typography,
    List,
    ListItem,
    ListItemText
} from "@material-ui/core";

const styles = {
    Paper: {
        padding: 20,
        marginTop: 10,
        marginBottom: 10,
        height: 300,
        overflowY: "auto"
    }
};

export default ({ exercises, category, onSelect }) => (
    <Grid container>
        <Grid item sm>
            <Paper style={styles.Paper}>
                {exercises.map(([group, exercises]) =>
                    !category || category === group ? (
                        <>
                            <Typography
                                style={{ textTransform: "capitalize" }}
                                variant="subtitle1"
                                key={group}
                            >
                                {group}
                            </Typography>
                            <List
                                component="ul"
                                aria-label="secondary mailbox folders"
                            >
                                {exercises.map(({ title }) => (
                                    <ListItem button>
                                        <ListItemText primary={title} onClick={() => onSelect}/>
                                    </ListItem>
                                ))}
                            </List>
                        </>
                    ) : null
                )}
            </Paper>
        </Grid>
        <Grid item sm>
            <Paper style={styles.Paper}>
                <Typography variant="h4" style={{ marginTop: 20 }}>
                    Welcome:
                </Typography>
                <Typography variant="subtitle1">
                    Please select an exercie from the list on the left
                </Typography>
            </Paper>
        </Grid>
    </Grid>
);
